files &lt;- list.files('data/raw/modis/MOD13A3.v061/enero',full.names = TRUE)

ndvi &lt;- files |&gt; 
  purrr::map(rast,lyrs=1) 

ndvi_ene &lt;- sprc(ndvi) |&gt; 
  mosaic() |&gt; 
  project('EPSG:32719') |&gt; 
  mask(vect(regs_utm)) |&gt; 
  trim() |&gt; 
  app(\(x) x*1e-8)

ndvi_ene &lt;- resample(ndvi_ene,r)
names(ndvi_ene) &lt;- 'ndvi_ene'
plot(ndvi_ene)
plot(regs_utm,add = TRUE)







# MOD11A2 LST Enero

tiles &lt;- c('h11v11','h11v12','h12v12')

lst_out &lt;- purrr::map(tiles,\(tile){
  files &lt;- list.files('data/raw/modis/MOD11A2.v061/enero',full.names = TRUE,pattern = tile)

  lst_regs &lt;- files |&gt; 
    purrr::map(rast,lyrs = 'LST_Day_1km') |&gt;
    rast() |&gt; 
    trim() |&gt; 
    app(mean)
})

lst_ene &lt;- lst_out |&gt; 
  sprc() |&gt; 
  mosaic() |&gt; 
  project("EPSG:32719") |&gt; 
  mask(vect(regs_utm)) |&gt; 
  trim() |&gt; 
  app(\(x) x-273.15)

lst_ene &lt;- resample(lst_ene,r)
